<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
<html xmlns="http://www.w3.org/1999/xhtml">
<head>
    <meta name="viewport" content="width=device-width, initial-scale=1.0" />
    <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />
    <style type="text/css">
        @media only screen and (max-width: 640px) {
            body{width:auto!important;}
            table[class=full2] {width: 100%!important; clear: both; }
            table[class=mobile2] {width: 100%!important; padding-left: 20px; padding-right: 20px; clear: both; }
            table[class=fullCenter2] {width: 100%!important; text-align: center!important; clear: both; }
            td[class=fullCenter2] {width: 100%!important; text-align: center!important; clear: both; }
        }

        @media only screen and (max-width: 479px) {
            .button {width: 100% !important;}
            body{width:auto!important;}
            table[class=full2] {width: 100%!important; clear: both; }
            table[class=mobile2] {width: 100%!important; padding-left: 20px; padding-right: 20px; clear: both; }
            table[class=fullCenter2] {width: 100%!important; text-align: center!important; clear: both; }
            td[class=fullCenter2] {width: 100%!important; text-align: center!important; clear: both; }
            table[class=full] {width: 100%!important; clear: both; }
            table[class=mobile] {width: 100%!important; padding-left: 20px; padding-right: 20px; clear: both; }
            table[class=fullCenter] {width: 100%!important; text-align: center!important; clear: both; }
            td[class=fullCenter] {width: 100%!important; text-align: center!important; clear: both; }
        }
    </style>
</head>
<body>
    <table class="full2 wrapper" bgcolor="#303030" border="0"  width="100%" cellpadding="0" cellspacing="0" align="center">
        <tr>
            <td align="center">
                <table width="100%" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2">
                    <tr>
                        <td width="100%" align="center">
                            <div>
                                <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="full2">
                                    <tr>
                                        <td width="600" height="50"></td>
                                    </tr>
                                </table>
                                <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="full2">
                                    <tr>
                                        <td width="600" height="50"></td>
                                    </tr>
                                </table>
                                <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 header-wrap" bgcolor="#4edeb5">
                                    <tr>
                                        <td width="600" valign="middle" align="center" class="logo">
                                            <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2 header-inner">
                                                <tr>
                                                    <td width="100%" height="30"></td>
                                                </tr>
                                                <tr>
                                                    <td width="100%">
                                                        <span><img src="http://lurncentral.lurntechnology.com/images/logo.png" width="75" alt="" border="0" /></span>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td valign="middle" width="100%" class="intro">
                                                        <!--[if !mso]><!--><span style="font-family: 'proxima_novathin', Helvetica; font-weight: normal;"><!--<![endif]--><!-- g -->
                                                            {{ $intro or '' }}
                                                        <!--[if !mso]><!--></span><!--<![endif]-->
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td width="100%" height="30"></td>
                                                </tr>
                                            </table>
                                        </td>
                                    </tr>
                                </table>
                            </div>
                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="full2 content-wrap">
                                <tr>
                                    <td width="600" bgcolor="#ffffff" align="center" class="content-wrap-cell">
                                        <div>
                                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 bg-white" bgcolor="#ffffff">
                                                <tr>
                                                    <td width="600" valign="middle" align="center">
                                                        <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2">
                                                            <tr>
                                                                <td width="100%" height="30"></td>
                                                            </tr>
                                                        </table>
                                                    </td>
                                                </tr>
                                            </table>
                                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 bg-white" bgcolor="#ffffff">
                                                <tr>
                                                    <td width="600" valign="middle" align="center">
                                                        <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2">
                                                            <tr>
                                                                <td valign="middle" width="100%" class="greeting">
                                                                    <!--[if !mso]><!--><span style="font-family: 'proxima_novathin', Helvetica; font-weight: normal;"><!--<![endif]--><!-- g -->
                                                                        {{ $greeting or '' }}
                                                                    <!--[if !mso]><!--></span><!--<![endif]-->
                                                                </td>
                                                            </tr>
                                                        </table>
                                                    </td>
                                                </tr>
                                            </table>
                                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 bg-white" bgcolor="#ffffff">
                                                <tbody>
                                                    <tr>
                                                        <td width="600" valign="middle" align="center">
                                                            <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2">
                                                                <tbody>
                                                                    <tr>
                                                                        <td valign="middle" width="100%" class="message before-cta">
                                                                            <!--[if !mso]><!--><span style="font-family: 'proxima_nova_rgregular', Helvetica; font-weight: normal;"><!--<![endif]-->
                                                                                <p>{{ $beforeCTA or '' }}</p>
                                                                            <!--[if !mso]><!--></span><!--<![endif]-->
                                                                        </td>
                                                                    </tr>
                                                                </tbody>
                                                            </table>
                                                        </td>
                                                    </tr>
                                                </tbody>
                                            </table>
                                            {{ $action or '' }}
                                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 bg-white" bgcolor="#ffffff">
                                                <tbody>
                                                    <tr>
                                                        <td width="600" valign="middle" align="center">
                                                            <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2">
                                                                <tbody>
                                                                    <tr>
                                                                        <td valign="middle" width="100%" class="message after-cta">
                                                                            <!--[if !mso]><!--><span style="font-family: 'proxima_nova_rgregular', Helvetica; font-weight: normal;"><!--<![endif]-->
                                                                                <p>{{ $afterCTA or '' }}</p>
                                                                            <!--[if !mso]><!--></span><!--<![endif]-->
                                                                        </td>
                                                                    </tr>
                                                                </tbody>
                                                            </table>
                                                        </td>
                                                    </tr>
                                                </tbody>
                                            </table>
                                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 bg-white" bgcolor="#ffffff">
                                                <tr>
                                                    <td width="600" valign="middle" align="center">
                                                        <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2">
                                                            <tr>
                                                                <td width="100%" height="30"> </td>
                                                            </tr>
                                                        </table>
                                                    </td>
                                                </tr>
                                            </table>
                                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 bg-white" bgcolor="#ffffff">
                                                <tbody>
                                                    <tr>
                                                        <td width="600" valign="middle" align="center">
                                                            <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2">
                                                                <tbody>
                                                                    <tr>
                                                                        <td valign="middle" width="100%" class="salutation message">
                                                                            <!--[if !mso]><!--><span style="font-family: 'proxima_nova_rgregular', Helvetica; font-weight: normal;"><!--<![endif]-->
                                                                                <p>{{ $salutation or '' }}</p>
                                                                            <!--[if !mso]><!--></span><!--<![endif]-->
                                                                        </td>
                                                                    </tr>
                                                                </tbody>
                                                            </table>
                                                        </td>
                                                    </tr>
                                                </tbody>
                                            </table>
                                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2 bg-white content-closer" bgcolor="#ffffff">
                                                <tr>
                                                    <td width="600" valign="middle" align="center" class="inner-wrap">
                                                        <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2 content-closer-inner">
                                                            <tr>
                                                                <td width="100%" height="50"></td>
                                                            </tr>
                                                        </table>
                                                    </td>
                                                </tr>
                                            </table>
                                        </div>
                                    </td>
                                </tr>
                            </table>
                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="full2">
                                <tr>
                                    <td width="600" height="30"></td>
                                </tr>
                            </table>
                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2">
                                <tbody>
                                    <tr>
                                        <td width="600" valign="middle" align="center">
                                            <table width="540" border="0" cellpadding="0" cellspacing="0" align="center" class="fullCenter2">
                                                <tbody>
                                                    <tr>
                                                        <td valign="middle" width="100%">
                                                            {{ $subcopy or '' }}
                                                        </td>
                                                    </tr>
                                                </tbody>
                                            </table>
                                        </td>
                                    </tr>
                                </tbody>
                            </table>
                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2">
                                <tr>
                                    <td width="600" height="30"></td>
                                </tr>
                            </table>
                            <table width="600" border="0" cellpadding="0" cellspacing="0" align="center" class="mobile2">
                                <tr>
                                    <td width="600" height="30"></td>
                                </tr>
                            </table>
                        </td>
                    </tr>
                    {{ $footer or '' }}
                </table>
            </td>
        </tr>
    </table>
</body>
</html>
